import * as React from 'react';

class Api extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: []
		}
	}
}

componentDidMount: () => {
	fetch('https://i8rmpiaad2.execute-api.us-east-1.amazonaws.com/dev/api/')
	.then(response => response.json())
	.then(artistasJson => this.setState({ artistas: artistasJson.result }))
}

render: () => {
	const artistas = this.state.artistas;
	return <Artistas todos = {artistas} />
}

 

export default Api;
