import * as React from 'react'
import Artista from './Artista'
import { getArtistas } from '../api/artists'

class Artistas extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: [],
			isFetch: true
		}
	}

	async componentDidMount (){
		const responseJson = await getArtistas()
		this.setState({artistas: responseJson, isFetch: false})
	}

	render(){
		const { isFetch, artistas } = this.state

		if(this.state.isFetch){
			return "Cargando..."
		}
		return(
			this.state.artistas.map((artista) => <Artista name={artista.name} key={artista.id} />)
		)
	}
}

 

export default Artistas;
