import * as React from 'react'
import PropTypes from 'prop-types'

class Artista extends React.Component{
	constructor(props){
		super(props)

		this.state={
			name: "",
			image: ""
		}
	}

	render(){
		const {verAlbums} = this.props
		return(
			<div className="artista" onClick={verAlbums}>
				<span>{name}</span>
				<img src={image} alt={name}/>
			</div>
		)
	}
}

Artista.propTypes = {
	name: PropTypes.string,
	image: PropTypes.string,
	id: PropTypes.number,
	verAlbums: PropTypes.func
}

export default Artista
