import * as React from 'react'
import { getArtistas } from '../api/artists'
import { getAlbumsByArtist } from '../api/albums'

class Artistas extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: [],
			albums: [],
			isFetch: true
		}
	}

	verArtistas = () =>{
		const { artistas } = this.state
			// this.state.artistas.pop(),
	}

	async componentDidMount (){
		const responseJson = await getArtistas()
		this.setState({artistas: responseJson, isFetch: false})
		this.render(this.verArtistas())
	}

	albumsQuery = async (id) => {
		const responseJson = await getAlbumsByArtist(id)
		this.setState({albums: responseJson, isFetch: false})
	}

	render(view){
		const { isFetch } = this.state
		if(this.state.isFetch){
			return "Cargando..."
		}
		return (
			<section className="container-fluid">
			{
				 this.state.artistas.map((artista) => 
						<div key={artista.id} className="artista" onClick={() => this.albumsQuery(artista.id)}>
							<span>{artista.name}</span>
							<img src={artista.image} alt={artista.name}/>
						</div>
					)
			}
			</section>
		)
	}
}

 

export default Artistas;
