import * as React from 'react'
//import PropTypes from 'prop-types'

class Api extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: [],
			isFetch: true
		}
	}

	componentDidMount (){
		fetch('https://i8rmpiaad2.execute-api.us-east-1.amazonaws.com/dev/api/artists')
		.then(response => response.json())
		.then(artistasJson => this.setState({ artistas: artistasJson, isFetch: false }));

	}

	render(){
		if(this.state.isFetch){
			return "Cargando..."
		}
		//const artista = this.state.artistas[0].name;
		return(
			this.state.artistas.map((artista) => <h2>{artista.name}</h2>)
		)
	}
}

 

export default Api;
