import * as React from 'react'
import Artista from './Artista'
import { getArtistas } from '../api/artists'

class Artistas extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: [],
			isFetch: true
		}
	}

	async componentDidMount (){
		const responseJson = await getArtistas()
		responseJson.map(function(i, responseJson) {
			if (Object.keys(responseJson).length === 9){
				this.setState({artistas: responseJson, isFetch: false})
			}
			return true;
		})
	}

	render(){
		const { isFetch, artistas } = this.state
		if(this.state.isFetch){
			return "Cargando..."
		}
		return(
			<section className="container-fluid">
				{
					this.state.artistas.map((artista) => <Artista {...artista} key={artista.id} />)
				}
			</section>
		)
	}
}

 

export default Artistas;
