import * as React from 'react'
import Artista from './Artista'

class Artistas extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: [],
			isFetch: true
		}
	}

	componentDidMount (){
		fetch('https://i8rmpiaad2.execute-api.us-east-1.amazonaws.com/dev/api/artists')
		.then(response => response.json())
		.then(artistasJson => this.setState({ artistas: artistasJson, isFetch: false }));

	}

	render(){
		const { isFetch, artistas } = this.state

		if(this.state.isFetch){
			return "Cargando..."
		}
		//const artista = this.state.artistas[0].name;
		return(
			this.state.artistas.map((artista) => <Artista name={artista.name} key={artista.id} />)
		)
	}
}

 

export default Artistas;
