import * as React from 'react'

class Api extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: []
		}

		coonsole.log(artistas);
	}

	componentDidMount (){
		fetch('https://i8rmpiaad2.execute-api.us-east-1.amazonaws.com/dev/api/')
		.then(response => response.json())
		.then(artistasJson => this.setState({ artistas: artistasJson.result }))

	}

	render(){
		return this.state.artistas
	}
}

 

export default Api;
