import * as React from 'react'
//import PropTypes from 'prop-types'

class Api extends React.Component{
	constructor(props){
		super(props)

		this.state={
			artistas: [],
			isFetch: true
		}
	}

	componentDidMount (){
		fetch('https://i8rmpiaad2.execute-api.us-east-1.amazonaws.com/dev/api/')
		.then(response => response.json())
		.then(artistasJson => this.setState({ artistas: artistasJson.result }));

	}

	render(){
		const artistas = this.state.artistas[0];
		return <Api artistas = {artistas} />;
	}
}

 

export default Api;
